+import { HiEye, HiEyeOff } from 'react-icons/hi';
+import { Button } from '@/components/ui/button';
+
+export default function SignupPage() {
+  const [username, setUsername] = useState('');
+  const [email, setEmail] = useState('');
+  const [password, setPassword] = useState('');
+  const [confirmPassword, setConfirmPassword] = useState('');
+  const [showPassword, setShowPassword] = useState(false);
+  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
+  const [error, setError] = useState('');
+
+ const handleSignup = async (e: React.FormEvent) => {
+  e.preventDefault();
+  setError('');
+
+  if (password !== confirmPassword) {
+    setError('Passwords do not match!');
+    return;
+  }
+
+  try {
+    // Signup request
+    const res = await fetch('http://localhost:8000/api/auth/registration/', {
+      method: 'POST',
+      headers: { 'Content-Type': 'application/json' },
+      body: JSON.stringify({
+        username: username.trim(),
+        email: email.trim(),
+        password1: password,
+        password2: confirmPassword,
+      }),
+    });
+
+    const data = await res.json();
+
+    if (res.ok) {
+      console.log('Signup successful!', data);
+
+      // Auto-login right after signup
+      const loginRes = await fetch('http://localhost:8000/api/auth/login/', {
+        method: 'POST',
+        headers: { 'Content-Type': 'application/json' },
+        body: JSON.stringify({
+          email: email.trim(),     // use email here
+          password: password,
+        }),
+      });
+
+      const loginData = await loginRes.json();
+
+      if (res.ok) {
+  console.log('Signup successful!', data);
+
+  console.log('Logging in with:', { email: email.trim(), password });
+
+  const loginRes = await fetch('http://localhost:8000/api/auth/login/', {
+    method: 'POST',
+    headers: { 'Content-Type': 'application/json' },
+    body: JSON.stringify({
+      email: email.trim(),
+      password: password,
+    }),
+  });
+
+  const loginData = await loginRes.json();
+
+  if (loginRes.ok) {
+    console.log('Auto-login successful!', loginData);
+    localStorage.setItem('token', loginData.access);
+    window.location.href = '/';
+  } else {
+    setError('Signup succeeded, but auto-login failed. Please login manually.');
+    console.error('Auto-login failed:', loginData);
+  }
+}
+};
+
+
+const handleGoogleSignup = () => {
+    console.log('Signup with Google clicked');
+  };
+
+  return (
+    <div className="min-h-screen flex items-center justify-center px-4 bg-gray-50">
+      <div className="w-full max-w-md space-y-6 bg-white p-6 rounded-lg shadow-md">
+        <h2 className="text-2xl font-bold text-center text-blue-700">Create an Account</h2>
+
+        <Button
+          onClick={handleGoogleSignup}
+          className="w-full flex items-center justify-center gap-2 border border-gray-300 bg-white text-gray-800 hover:bg-gray-100"
+          variant="outline"
+        >
+          <FcGoogle size={20} /> Continue with Google
+        </Button>
+
+        <div className="relative my-4">
+          <div className="absolute inset-0 flex items-center">
+            <div className="w-full border-t border-gray-300" />
+          </div>
+          <div className="relative flex justify-center text-sm text-gray-500 bg-white px-2">
+            or sign up with email
+          </div>
+        </div>
+
+        <form onSubmit={handleSignup} className="space-y-4">
+          {/* Username */}
+          <div>
+            <label htmlFor="username" className="text-sm font-medium text-gray-700">
+              Username
+            </label>
+            <input
+              type="text"
+              id="username"
+              value={username}
+              required
+              onChange={(e) => setUsername(e.target.value)}
+              className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:ring-blue-200"
+              placeholder="yourusername"
+            />
+          </div>
+
+          {/* Email */}
+          <div>
+            <label htmlFor="email" className="text-sm font-medium text-gray-700">
+              Email
+            </label>
+            <input
+              type="email"
+              id="email"
+              value={email}
+              required
+              onChange={(e) => setEmail(e.target.value)}
+              className="w-full mt-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:ring-blue-200"
+              placeholder="you@example.com"
+            />
+          </div>
+
+          {/* Password */}
+          <div className="relative">
+            <label htmlFor="password" className="text-sm font-medium text-gray-700">
+              Password
+            </label>
+            <input
+              type={showPassword ? 'text' : 'password'}
+              id="password"
+              value={password}
+              required
+              onChange={(e) => setPassword(e.target.value)}
+              className="w-full mt-1 pr-10 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:ring-blue-200"
+              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
+            />
+            <button
+              type="button"
+              onClick={() => setShowPassword(!showPassword)}
+              className="absolute top-[38px] right-3 text-gray-500 hover:text-gray-700 focus:outline-none"
+              aria-label={showPassword ? 'Hide password' : 'Show password'}
+            >
+              {showPassword ? <HiEyeOff size={20} /> : <HiEye size={20} />}
+            </button>
+          </div>
+
+          {/* Confirm Password */}
+          <div className="relative">
+            <label htmlFor="confirmPassword" className="text-sm font-medium text-gray-700">
+              Confirm Password
+            </label>
+            <input
+              type={showConfirmPassword ? 'text' : 'password'}
+              id="confirmPassword"
+              value={confirmPassword}
+              required
+              onChange={(e) => setConfirmPassword(e.target.value)}
+              className="w-full mt-1 pr-10 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring focus:ring-blue-200"
+              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
+            />
+            <button
+              type="button"
+              onClick={() => setShowConfirmPassword(!showConfirmPassword)}
+              className="absolute top-[38px] right-3 text-gray-500 hover:text-gray-700 focus:outline-none"
+              aria-label={showConfirmPassword ? 'Hide confirm password' : 'Show confirm password'}
+            >
+              {showConfirmPassword ? <HiEyeOff size={20} /> : <HiEye size={20} />}
+            </button>
+          </div>
+
+          {/* Error Message */}
+          {error && (
+            <p className="text-sm text-red-600 text-center">{error}</p>
+          )}
+
+          <Button type="submit" className="w-full">
+            Sign Up
+          </Button>
+        </form>
+
+        <p className="text-center text-sm text-gray-500">
+          Already have an account?{' '}
+          <Link href="/login" className="text-blue-600 hover:underline">
+            Login here
+          </Link>
+        </p>
+      </div>
+    </div>
+  );
+}

