+import { useState } from 'react';
+import Link from 'next/link';
+import { FcGoogle } from 'react-icons/fc';
+import { HiEye, HiEyeOff } from 'react-icons/hi';
+import { Button } from '@/components/ui/button';
+
+export default function SignupPage() {
+  const [username, setUsername] = useState('');
+  const [email, setEmail] = useState('');
+  const [password, setPassword] = useState('');
+  const [confirmPassword, setConfirmPassword] = useState('');
+  const [showPassword, setShowPassword] = useState(false);
+  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
+  const [error, setError] = useState('');
+
+const handleSignup = async (e: React.FormEvent) => {
+  e.preventDefault();
+  setError('');
+
+  if (password !== confirmPassword) {
+    setError('Passwords do not match!');
+    return;
+  }
+
+  try {
+    // Signup request
+    const res = await fetch('http://localhost:8000/api/auth/registration/', {
+      method: 'POST',
+      headers: { 'Content-Type': 'application/json' },
+      body: JSON.stringify({
+        username: username.trim(),
+        email: email.trim(),
+        password1: password,
+        password2: confirmPassword,
+      }),
+    });
+
+    const data = await res.json();
+
+    if (res.ok) {
+      console.log('Signup successful!', data);
+
+      // Debug log to verify values sent to login
+      console.log('Attempting auto-login with:', {
+        email: email.trim(),
+        password,
+      });
+
+      // Auto-login right after signup with ONLY email and password
+      const loginRes = await fetch('http://localhost:8000/api/auth/login/', {
+        method: 'POST',
+        headers: { 'Content-Type': 'application/json' },
+        body: JSON.stringify({
+          email: email.trim(),
+          password: password,
+        }),
+      });
+
+      const loginData = await loginRes.json();
+
+      if (loginRes.ok) {
+        console.log('Auto-login successful!', loginData);
+        localStorage.setItem('token', loginData.access);
+        window.location.href = '/';
+      } else {
+        setError('Signup succeeded, but auto-login failed. Please login manually.');
+        console.error('Auto-login failed:', loginData);
+      }
+    } else {
+      // Format signup errors nicely
+      let messages: string[] = [];
+      for (let field in data) {
+        messages.push(`${field}: ${data[field].join(' ')}`);
+      }
+      setError(messages.join(' | '));
+    }
+  } catch (err) {
+    console.error(err);
+    setError('Something went wrong!');
+  }
+};
+
+
+const handleGoogleSignup = () => {
+    console.log('Signup with Google clicked');
+  };
+
