+      method: 'POST',
+      headers: { 'Content-Type': 'application/json' },
+      body: JSON.stringify({
+        username: username.trim(),
+        email: email.trim(),
+        password1: password,
+        password2: confirmPassword,
+      }),
+    });
+
+    const data = await res.json();
+
+    if (res.ok) {
+      console.log('Signup successful!', data);
+
+      // Debug log to verify values sent to login
