+
+      console.log('Signup successful');
+
+      // Now, login immediately after signup
+      await axios.post(
+        'http://localhost:8000/auth/login/', // Adjust to your login endpoint
+        {
+          email: email.trim(),
+          password: password,
+        },
+        {
+          headers: {
+            'Content-Type': 'application/json',
+            'X-CSRFToken': csrfToken || '',
+          },
+        }
+      );
+
+      console.log('Login successful after signup');
+
+      // Redirect to homepage or dashboard after auto-login
+      router.push('/'); // or '/' or wherever you want
+
+    } catch (err: any) {
+      console.error(err);
+
+      if (err.response?.data) {
+        const data = err.response.data;
+        let messages: string[] = [];
+
+        for (const key in data) {
+          if (Array.isArray(data[key])) {
+            messages.push(`${key}: ${data[key].join(' ')}`);
+          } else if (typeof data[key] === 'string') {
+            messages.push(`${key}: ${data[key]}`);
