+"use client";
+
+import { useEffect, useState } from "react";
+import { FaClock, FaCheckCircle, FaMotorcycle, FaUtensils } from "react-icons/fa";
+
+interface Order {
+  id: number;
+  status: string;
+  ordered_at: string;
+  confirmed_at?: string | null;
+  preparing_at?: string | null;
+  prepare_duration?: number; // in minutes (set by staff)
+  [key: string]: any;
+}
+
+interface Props {
+  trackingOrders: Order[];
+}
+
+const OrderTrackingTab: React.FC<Props> = ({ trackingOrders }) => {
+  const [progressData, setProgressData] = useState<any[]>([]);
+
+  useEffect(() => {
+    const interval = setInterval(() => {
+      const updated = trackingOrders.map((order) => {
+        const now = new Date().getTime();
+        let stage = "pending";
+        let startTime = new Date(order.ordered_at).getTime();
+        let duration = 2 * 60 * 1000;
+
+        if (order.status === "confirmed" && order.confirmed_at) {
+          stage = "confirmed";
+          startTime = new Date(order.confirmed_at).getTime();
+          duration = 2 * 60 * 1000;
+        }
+
+        if (order.status === "preparing" && order.preparing_at && order.prepare_duration) {
+          stage = "preparing";
+          startTime = new Date(order.preparing_at).getTime();
+          duration = order.prepare_duration * 60 * 1000;
+        }
+
+        const elapsed = now - startTime;
+        const progress = Math.min(100, (elapsed / duration) * 100);
+        const remaining = Math.max(0, Math.ceil((duration - elapsed) / 60000));
+
+        return {
+          ...order,
+          stage,
+          progress,
+          remainingMin: remaining,
+        };
+      });
+
+      setProgressData(updated);
+    }, 1000);
+
+    return () => clearInterval(interval);
+  }, [trackingOrders]);
+
+  const getStageIcon = (stage: string) => {
+    if (stage === "pending") return <FaClock className="text-yellow-500 mr-2" />;
+    if (stage === "confirmed") return <FaCheckCircle className="text-blue-500 mr-2" />;
+    if (stage === "preparing") return <FaUtensils className="text-green-600 mr-2" />;
+    return null;
+  };
+
+  const getMovingEmoji = (stage: string) => {
+    if (stage === "preparing") return "üçΩÔ∏è";
+    if (stage === "confirmed") return "üèçÔ∏è";
+    if (stage === "pending") return "‚è≥";
+    return "";
+  };
+
+  return (
+    <div className="bg-white shadow rounded-lg p-6">
+      <h2 className="text-2xl font-bold mb-4 text-blue-700">üì¶ Track Your Order</h2>
+
+      {progressData.length === 0 ? (
+        <p className="text-gray-400 text-sm">No active orders to track.</p>
+      ) : (
+        <div className="flex flex-col space-y-6">
+          {progressData.map((order, index) => (
+            <div key={order.id || index} className="bg-gray-50 p-4 border rounded-lg shadow relative overflow-hidden">
+              <div className="flex justify-between items-center mb-2">
+                <div className="flex items-center">
+                  {getStageIcon(order.stage)}
+                  <p className="font-semibold text-gray-800">Order #{order.id}</p>
+                </div>
+                <span className="text-sm text-gray-600">Status: {order.status}</span>
+              </div>
+
+              <p className="text-sm text-gray-600 mb-1">
+                {order.remainingMin > 0
+                  ? `Estimated Time: ${order.remainingMin} min`
+                  : "Almost Ready!"}
+              </p>
+
+              <div className="relative w-full bg-gray-300 h-3 rounded-full overflow-hidden mt-2">
+                <div
+                  className={`h-full rounded-full transition-all duration-500 ${
+                    order.progress < 100
+                      ? order.stage === "preparing"
+                        ? "bg-green-500"
+                        : "bg-blue-500"
+                      : "bg-emerald-500"
+                  }`}
+                  style={{ width: `${order.progress}%` }}
+                />
+
+                {/* Moving emoji */}
+                {order.progress > 0 && (
+                  <div
+                    className="absolute -top-5 text-xl transition-all duration-300"
+                    style={{ left: `calc(${order.progress}% - 10px)` }}
+                  >
+                    {getMovingEmoji(order.stage)}
+                  </div>
+                )}
+
+                {/* Skeleton shimmer for 0% progress */}
+                {order.progress === 0 && (
+                  <div className="absolute inset-0 animate-pulse bg-gradient-to-r from-transparent via-white to-transparent" />
+                )}
+              </div>
+            </div>
+          ))}
+        </div>
+      )}
+    </div>
+  );
+};
+
+export default OrderTrackingTab;

