+"use client";
+
+import { useEffect, useState } from "react";
+import { FaBiking, FaUtensils } from "react-icons/fa"; // Bike & Food icons
+
+interface Order {
+  id: number;
+  status: string;
+  ordered_at: string;
+  confirmed_at: string | null;
+  preparing_at: string | null;
+  preparing_duration: number | null;
+  [key: string]: any;
+}
+
+interface Props {
+  trackingOrders: Order[];
+}
+
+const OrderTrackingTab: React.FC<Props> = ({ trackingOrders }) => {
+  const [progressData, setProgressData] = useState<Order[]>([]);
+
+  useEffect(() => {
+    const interval = setInterval(() => {
+      const updated = trackingOrders.map((order) => {
+        const now = new Date().getTime();
+        let baseTime = 0;
+        let duration = 0;
+        let progress = 0;
+        let remainingMin = 0;
+
+        if (order.status === "pending") {
+          baseTime = new Date(order.ordered_at).getTime();
+          duration = 2 * 60 * 1000;
+          progress = Math.min(100, ((now - baseTime) / duration) * 100);
+          remainingMin = Math.max(0, 2 - Math.floor((now - baseTime) / 60000));
+        } else if (order.status === "confirmed" && order.confirmed_at) {
+          baseTime = new Date(order.confirmed_at).getTime();
+          duration = 2 * 60 * 1000;
+          progress = Math.min(100, ((now - baseTime) / duration) * 100);
+          remainingMin = Math.max(0, 2 - Math.floor((now - baseTime) / 60000));
+        } else if (order.status === "preparing" && order.preparing_at && order.preparing_duration) {
+          baseTime = new Date(order.preparing_at).getTime();
+          duration = order.preparing_duration * 60 * 1000;
+          progress = Math.min(100, ((now - baseTime) / duration) * 100);
+          remainingMin = Math.max(0, order.preparing_duration - Math.floor((now - baseTime) / 60000));
+        }
+
+        return {
+          ...order,
+          progress,
+          remainingMin,
+        };
+      });
+
+      setProgressData(updated);
+    }, 1000);
+
+    return () => clearInterval(interval);
+  }, [trackingOrders]);
+
+  return (
+    <div className="bg-white shadow rounded-lg p-6">
+      <h2 className="text-2xl font-bold mb-4 text-blue-700">üì¶ Track Your Order</h2>
+
+      {progressData.length === 0 && (
+        <p className="text-gray-500 text-sm">No active orders to track.</p>
+      )}
+
+      <div className="flex flex-col space-y-5">
+        {progressData.map((order, index) => (
+          <div
+            key={order.id || index}
+            className="relative bg-gradient-to-r from-gray-50 to-white border rounded-lg p-4 shadow"
+          >
+            <div className="flex justify-between items-center mb-2">
+              <p className="font-semibold text-lg text-gray-800">Order #{order.id}</p>
+              <span className="text-sm text-gray-500 capitalize">Status: {order.status}</span>
+            </div>
+
+            <p className="text-sm text-gray-600 mb-3">
+              {order.remainingMin > 0
+                ? `Estimated Ready In: ${order.remainingMin} min`
+                : `Almost Ready!`}
+            </p>
+
+            {/* Bike or Food icon moving above progress bar */}
+            <div className="relative h-8 mb-1">
+              <div
+                className="absolute top-0 transition-all duration-500"
+                style={{
+                  left: `calc(${order.progress}% - 16px)`,
+                }}
+              >
+                {order.status === "preparing" ? (
+                  <FaUtensils className="text-green-600 text-xl animate-pulse" />
+                ) : (
+                  <FaBiking className="text-blue-500 text-xl animate-pulse" />
+                )}
+              </div>
+            </div>
+
+            {/* Progress Bar */}
+            <div className="w-full bg-gray-300 h-3 rounded-full overflow-hidden">
+              <div
+                className={`h-full rounded-full transition-all duration-500 ${
+                  order.status === "preparing"
+                    ? "bg-green-500"
+                    : order.progress < 100
+                    ? "bg-blue-500"
+                    : "bg-emerald-600"
+                }`}
+                style={{ width: `${order.progress}%` }}
+              />
+            </div>
+
+            {/* Skeleton Pulse if progress is 0 */}
+            {order.progress === 0 && (
