"use client";

import { useEffect, useState } from "react";

interface Order {
  id: number;
  status: string;
  confirmed_at: string | null;
  [key: string]: any; // to support additional props
}

interface Props {
  trackingOrders: Order[];
}

const OrderTrackingTab: React.FC<Props> = ({ trackingOrders }) => {
  const [progressData, setProgressData] = useState<Order[]>([]);

  useEffect(() => {
    const interval = setInterval(() => {
      const updated = trackingOrders.map(order => {
        if (!order.confirmed_at || order.status !== "confirmed")
          return { ...order, progress: 0, remainingMin: 10 };

        const confirmedTime = new Date(order.confirmed_at).getTime();
        const now = new Date().getTime();
        const elapsed = now - confirmedTime;
        const total = 10 * 60 * 1000;

        const progress = Math.min(100, (elapsed / total) * 100);
        const remaining = Math.max(0, 10 - Math.floor(elapsed / 60000));

        return {
          ...order,
          progress,
          remainingMin: remaining,
        };
      });

      setProgressData(updated);
    }, 1000);

    return () => clearInterval(interval);
  }, [trackingOrders]);

  return (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-2xl font-bold mb-4 text-blue-700">üì¶ Track Your Order</h2>

      {progressData.length === 0 && (
        <p className="text-gray-500 text-sm">No active orders to track.</p>
      )}

      <div className="flex flex-col space-y-5">
        {progressData.map((order, index) => (
          <div
            key={order.id || index}
            className="bg-gradient-to-r from-gray-50 to-white border rounded-lg p-4 shadow"
          >
            <div className="flex justify-between items-center mb-1">
              <p className="font-semibold text-lg text-gray-800">
                Order #{order.id}
              </p>
              <span className="text-sm text-gray-500">
                Status: {order.status}
              </span>
            </div>
            <p className="text-sm text-gray-600">
              {order.remainingMin > 0
                ? `Estimated Ready In: ${order.remainingMin} min`
                : `Almost Ready!`}
            </p>
            <div className="mt-2 w-full bg-gray-300 h-3 rounded-full overflow-hidden">
              <div
                className={`h-full rounded-full transition-all duration-500 ${
                  order.progress < 100 ? "bg-blue-500" : "bg-green-500"
                }`}
                style={{ width: `${order.progress}%` }}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrderTrackingTab;
